import Switch from "react-switch";
import {Component}from 'react';
import TextField from "@material-ui/core/TextField"
import { makeStyles } from '@material-ui/core/styles';
import './App.css';
import swap from './Icons/swap.svg';
import go from './Icons/go.svg';
import bathroom from './Icons/bathroom.svg';
import food from './Icons/food.svg';
import BluePhone from './Icons/phone.svg';

//Slider creation class
class ThemeSwitch extends Component {
  constructor() { //Creating the slider
    super();
    this.state = { checked: false };
    this.handleChange = this.handleChange.bind(this);
  }
  handleChange(checked) { //What will happen on state change
    this.setState({ checked });
    if(checked){
      var div = document.getElementById("background");
      div.className = "AppDark"
    }
    else{
      var div = document.getElementById("background");
      div.className = "AppLight"
    }
  }
  render() { //What the slider will look like
    return (
          <Switch
            checked={this.state.checked}
            onChange={this.handleChange}
            onColor="#4B4D4F"
            onHandleColor="#2B2D2F"
            handleDiameter={30}
            uncheckedIcon={false}
            checkedIcon={false}
            boxShadow="0px 1px 5px rgba(0, 0, 0, 0.6)"
            activeBoxShadow="0px 0px 1px 10px rgba(0, 0, 0, 0.2)"
            height={20}
            width={48}
            className="Switch"
          />
    )
  }
}

<ReactHover options={optionsCursorTrueWithMargin}>
  <Trigger type='trigger'>
    <h1 style={{ background: '#44B39D', width: '200px', fontSize: '1.5em', color: 'white' }}> <p>Hover on me</p></h1>
  </Trigger>
  <Hover type='hover'>
    <div className={componentstyles.hover}>
      <img className={componentstyles.thumbnail} alt="Albert Einstein" src="https://images.gr-assets.com/authors/1429114964p2/9810.jpg" />
      <blockquote className={componentstyles.quote}> Two things are infinite: the universe and human stupidity; and I'm not sure about the universe. </blockquote>
      <p className={componentstyles.people}>--Albert Einstein</p>
    </div>
  </Hover>
</ReactHover>

function App(){

  return (
    <body className="AppLight" id="background">
      <div className="SidePanel">
        <h1>Raider Maps</h1> {/*Heading*/}

        {/*From TextField*/}
        <div className="inputArea">
          <TextField label="From:" variant="filled" fullWidth margin="dense" style={{backgroundColor: "#FFFFFF",}} InputLabelProps={{shrink: true,}}></TextField>
        </div>

        {/*Swap 'From' and 'To' TextFields */}
        <div>
          <button className="SwapButton"><img src={swap} alt="Swap 'From' and 'To' Locations"/></button>
        </div>

        {/*To TextField */}
        <div className="inputArea">
          <TextField label="To:" variant="filled" fullWidth margin="dense" style={{backgroundColor: "#FFFFFF"}} InputLabelProps={{shrink: true,}}></TextField>
        </div>

        {/*Navigate button to give the path from the 'From' TextField to the 'To' TextField */}
        <div className="buttonHolder">
          <button className="GoButton"><img src={go} alt="Calculate directions and start journey"/></button>

          {/*Middle buttons below 'Navigate" Button */}
          <button className="midButtons"><img src={bathroom} alt="Route to the closest Bathroom"/></button>
          <ReactHover options={optionsCursorTrueWithMargin}>
          <Trigger type='trigger'>
            <h1 style={{ background: '#44B39D', width: '200px', fontSize: '1.5em', color: 'white' }}> <p>Hover on me</p></h1>
          </Trigger>
          <Hover type='hover'>
            <div className={componentstyles.hover}>
              <img className={componentstyles.thumbnail} alt="Albert Einstein" src="https://images.gr-assets.com/authors/1429114964p2/9810.jpg" />
              <blockquote className={componentstyles.quote}> Two things are infinite: the universe and human stupidity; and I'm not sure about the universe. </blockquote>
              <p className={componentstyles.people}>--Albert Einstein</p>
            </div>
          </Hover>
        </ReactHover>

          <button className="midButtons"><img src={food} alt="Route to the closest food location"/></button>

          <button className="midButtons"><img src={BluePhone} alt="Route to the closest Blue Phone"/></button>

        </div>



        {/*Theme switching slider*/}
        <div id="ThemeSwitch" >
          <span>
            Theme: 
          </span> 
          <ThemeSwitch/>
        </div>

      </div>
    </body>
  );
}

export default App;
